import Nav from "./Nav";
import { useDispatch, useSelector } from 'react-redux'
import Tarjeta from "./Tarjeta";
import s from "../css/principal.module.css"
import { Link } from "react-router-dom";
import React, { useState, useEffect } from 'react';
import { buscarPokemon, obtenerPokemons,siguiente,anterior } from "../../Redux/actions";
import Paginado from "./Paginado";
import axios from "axios"
const API_URL="https://pokeapi.co/api/v2/pokemon/?offset=20&limit=10"
const Principal = () => {

    const pokemon= useSelector((state) => state.pokemones)

    const [next, setNext] = useState(siguiente);
    const [prev, setPrev] = useState(anterior);

     //*paginado//
     const [pag,setPag] = useState(1)
     const [postPag] = useState(10)

     const lastCountry = pag * postPag;
     const firstCountry = lastCountry - postPag;
     /////
    const dispatch = useDispatch()
 
    useEffect(() => {
      dispatch(obtenerPokemons(API_URL))
   
 
       }, [])

    const siguientePag=()=>{
    dispatch(obtenerPokemons(next))
   
    setNext(next+1)
    }

    const anteriorPag=()=>{
        dispatch(obtenerPokemons(prev))
        setPrev(prev-1)
        }
    return ( 
    <div className={s.contenedorPrincipal}>
         <Nav/>
         <div className={s.container}>
            <div className={s.grid}>
              {   
                pokemon.length ?
               pokemon.map(c=>(
                <Link key={c.id} className={s.link} to={"/" + c.id}>
                    <Tarjeta
                    name={ c.name}
                    imagen={ c.sprites.other.dream_world.front_default}
                    type={ c.types.map(t=>t.type.name)}
                    />
                </Link>
            )
            ): <p>cargando...</p>
        } 
            </div>
            <div>
            <button className="btns" onClick={anteriorPag} >Anterior</button>
            <button className="btns" onClick={siguientePag} >Siguiente</button>
       </div>
        </div>   
    </div> 
    );
}
 
export default Principal;
