import Nav from "./Nav";
import { useDispatch, useSelector } from 'react-redux'
import Tarjeta from "./Tarjeta";
import s from "../css/principal.module.css"
import { Link } from "react-router-dom";
import React, { useState, useEffect } from 'react';
import { buscarPokemon, obtenerPokemons ,siguiente,ant} from "../../Redux/actions";
import Paginado from "./Paginado";
const API_URL="https://pokeapi.co/api/v2/pokemon/"
const Principal = () => {

    const pokemon= useSelector((state) => state.pokemones)
    const [next, setNext] = useState("");
    const [prev, setPrev] = useState("");
    const dispatch = useDispatch()
 

    useEffect(() => {
    dispatch(obtenerPokemons(API_URL))
       }, [])

    return ( 
    <div className={s.contenedorPrincipal}>
         <Nav/>
         <div className={s.container}>
            <div className={s.grid}>
                {pokemon.map(c=>(
                <Link key={c.id} className={s.link} to={"/" + c.id}>
                    <Tarjeta
                    name={ c.name}
                    imagen={ c.sprites.other.dream_world.front_default}
                    type={ c.types.map(t=>t.type.name)} 
                    />
                </Link>
            )
            )}
            </div>
            <div>
            <button className="btns" >Anterior</button>
            <button className="btns" onClick={()=>dispatch(obtenerPokemons(siguiente))}>Siguiente</button>
       </div>
        </div>   
    </div> 
    );
}
 
export default Principal;
