import Nav from "./Nav"
import { useState, useEffect } from "react"
import { useDispatch ,useSelector} from "react-redux"
import { useParams } from "react-router-dom"
import { detallePokemon } from "../../Redux/actions"
import s from "../css/detalle.module.css"

export default function Detalle() {

    const detail = useSelector((state)=> state.detail)
    let {id}= useParams()
    let dispatch= useDispatch()
    
    useEffect(() => {
      dispatch(detallePokemon(id))
      
    },[]) 
  
        if(detail.length===0){

            return(
                <div className={s.loadingDiv}>
            
                    <p className={s.spinner}></p>
                    <p className={s.loadingp}>Loading...</p>
                </div>
            )
        }

     return(
        <>
      <Nav/>
      <div className={s.container}>
           <img className={s.img} src={ detail.sprites.other.dream_world.front_default} alt="not image"/>
       <div className={s.items}>   
            <h2 className={s.name}>{detail.name}</h2> 
            <h5><b>ID:</b>{detail.id}</h5> 
            <h5> <b>Vida:</b> { detail.stats[0].base_stat}</h5> 
            <h5><b>Ataque:</b> { detail.stats[1].base_stat}</h5>
            <h5><b>Defensa:</b> { detail.stats[2].base_stat}</h5>
            <h5><b>Velocidad:</b> {detail.stats[3].base_stat}</h5>
            <h5><b>Altura:</b> {detail.height}</h5>
            <h5><b>Peso:</b> {detail.weight}</h5>
            <input type="range" name="" id="" defaultValue = {(rangeElem.max < rangeElem.min) ? rangeElem.min
               : rangeElem.min + (rangeElem.max - rangeElem.min)/2} />
        </div> 
        <h3 className={s.titAct}>Tipos:</h3>
       {/*  {detail.types.length?
            detail.types.map(e=>{
            return (
                <div className={s.actcontainer}>
                <h5><b>Tipo:</b> {e.type.name}</h5>
                </div>
            )
        }) : <div> <h3 className={s.dontact}>There are no registered activities</h3></div> */}
    
    </div>  
        </>
    )
};
