import { TYPES } from "./actions"
const initialState={
  pokemones:[],

}
export default function rootReducer(state = initialState, action) {

    switch (action.type) {
        case TYPES.OBTENER_POKEMON:
        return{
           ...state,
          pokemones: action.payload
        }
        case TYPES.BUSCAR_POKEMON:
        return{
          ...state,
         pokemones: action.payload.length
       }
        default:
          return state;
    }
  
}
