export const TYPES={
   OBTENER_POKEMON:"OBTENER_POKEMON",
   BUSCAR_POKEMON:"BUSCAR_POKEMON",
   DETALLE_POKEMON :"DETALLE_POKEMON",

}
export function obtenerPokemons(){

    function traerPoke({numero}) {
        return function (dispatch){
          fetch(`https://pokeapi.co/api/v2/pokemon/${numero}`)
          .then(respuesta=>respuesta.json())
          .then(pokemons=> {
            console.log(pokemons)
            dispatch({
                  type: TYPES.OBTENER_POKEMON,
                  payload: pokemons.results
                 
                })
          })
        }
        
    }
    let arrayPokes=[]
    for (let i = 1; i <=20; i++) { 
        const pokemons =  tr(i)//le agrego uno por uno de los indices para hacer el pedido 
        arrayPokes.push(pokemons) //agrego cada uno al array 
        }
        return{
            props:{
            arrayPokes
            }
        
}
}
