import React, { useState ,useEffect} from 'react';
import {Link} from "react-router-dom"
import s from "../css/nav.module.css"
import logo from "../../img/logo.png"
import { useDispatch } from 'react-redux';
import { buscarPokemon, Sort } from '../../Redux/actions';
import Paginado from './Paginado';
import { useHistory } from "react-router-dom";

const Nav = () => {

const [search, setSearch] = useState('');
 const dispatch = useDispatch()
 let history = useHistory ();
 
const handleChange=(e)=>{
    setSearch(e.target.value)
}
function orderAZ(e){
    dispatch(Sort(e.target.value))
    history.push("/home") //para que actualice
  }
 const handleSubmit=(e)=>{
    e.preventDefault();
    dispatch(buscarPokemon(search))
} 
    return ( 
    <header className={s.contenedorNav}>
        <nav className={s.nav}>
            <Link to={"/home"}>
            <img className={s.logo} src={logo}></img>
            </Link>
            <form className={s.form} onSubmit={handleSubmit}>
                <input className={s.buscador} placeholder="Buscar Pokemon.." value={search} onChange={handleChange} />
                <button className={s.submit} type="submit"> Buscar</button>
            </form>
            <h3  className={s.title}>Filter for: </h3>
      <select className={s.filterAndOrder}
          onChange={ 
           orderAZ
          }
        >
          <option>Alphabetical</option>
          <option value="ASCENDENTE"> A-Z </option>
          <option value="DESCENDENTE"> Z-A </option>
        </select>
          {/*   <div>
                < Link className={s.linkCrear} to={"/create"}>Crea tu Pokemon!</Link>
            </div> */}
        </nav>
    </header>
    );
}
 
export default Nav;
