import Nav from "./Nav";
import { useDispatch, useSelector } from 'react-redux'
import Tarjeta from "./Tarjeta";
import s from "../css/principal.module.css"
import { Link } from "react-router-dom";
import React, { useState, useEffect } from 'react';
import { buscarPokemon, obtenerPokemons,siguiente } from "../../Redux/actions";
import Paginado from "./Paginado";
import axios from "axios"
const API_URL="https://pokeapi.co/api/v2/pokemon/?offset=20&limit=10"
const Principal = () => {

    const pokemon= useSelector((state) => state.pokemones)
    const [results, setResults] = useState("");
    const [next, setNext] = useState(siguiente);
    const [prev, setPrev] = useState("");

    const dispatch = useDispatch()
 
    useEffect(() => {
      dispatch(obtenerPokemons(API_URL))
      
      

       }, [])

    const siguiente=()=>{
    dispatch(obtenerPokemons(next))
    setNext(prev=>prev)
    }


    return ( 
    <div className={s.contenedorPrincipal}>
         <Nav/>
         <div className={s.container}>
            <div className={s.grid}>
              {   
                pokemon.length ?
               pokemon.map(c=>(
                <Link key={c.id} className={s.link} to={"/" + c.id}>
                    <Tarjeta
                    name={ c.name}
                    imagen={ c.sprites.other.dream_world.front_default}
                    type={ c.types.map(t=>t.type.name)} 
                    />
                </Link>
            )
            ): <p>cargando...</p>
        } 
            </div>
            <div>
            <button className="btns" >Anterior</button>
            <button className="btns"  >Siguiente</button>
       </div>
        </div>   
    </div> 
    );
}
 
export default Principal;
