import axios from "axios"
export const siguiente =[]
export const anterior =[]
export const TYPES={
   OBTENER_POKEMON:"OBTENER_POKEMON",
   BUSCAR_POKEMON:"BUSCAR_POKEMON",
   DETALLE_POKEMON :"DETALLE_POKEMON",
   SIGUIENTE_PAG :"SIGUIENTE_PAG",
}
const API_URL="https://pokeapi.co/api/v2/pokemon/?offset=20&limit=10"

export function obtenerPokemons(url){
    return function (dispatch){
        fetch(url)
        .then(respuesta=>respuesta.json())
        .then(pokemons=> {
            siguiente.push(pokemons.next)
            anterior.push(pokemons.previous)
         
        //hago un fetch a todad las urls
         Promise.all(pokemons.results.map(p=>fetch(p.url))).then(responses =>
            //el resultado los covierto a objeto
            Promise.all(responses.map(res => res.json()))
        ).then(final => { 
            //finalmete los devuelvo 
            dispatch({
                    type: TYPES.OBTENER_POKEMON,
                    payload: final
                    
                })
            })  
        })
        
    
    }
}

export  function nextPoke(){
    const next= obtenerPokemons(siguiente)
    Promise.all(ne.results.map(p=>fetch(p.url))).then(responses =>
        //el resultado los covierto a objeto
        Promise.all(responses.map(res => res.json()))
    ).then(final => { 
        //finalmete los devuelvo 
        dispatch({
                type: TYPES.OBTENER_POKEMON,
                payload: final
                
            })
        })  
    console.log(next);
}
nextPoke()




export  function buscarPokemon(nombre) {
    return  function (dispatch) {
        try {
          fetch(`https://pokeapi.co/api/v2/pokemon/${nombre}` )
          .then(response=>response.json())
          .then(poke =>{
            const array=[]
            array.push(poke)
            dispatch({
              type:TYPES.BUSCAR_POKEMON,
              payload:array
            })  
          })
        } catch (error) {
          
            console.log(error)
        }
    }
  }
