import Nav from "./Nav";
import { useDispatch, useSelector } from 'react-redux'
import Tarjeta from "./Tarjeta";
import s from "../css/principal.module.css"
import { Link } from "react-router-dom";
import React, { useState, useEffect } from 'react';
import { buscarPokemon, obtenerPokemons } from "../../Redux/actions";
import Paginado from "./Paginado";

const Principal = () => {

    const pokemon= useSelector((state) => state.pokemones)
    const dispatch = useDispatch()
     //*paginado//
     const [pag,setPag] = useState(1)
     const [porPag] = useState(10)

     const lastIndex = pag * porPag;
     const firtsIndex = lastIndex- porPag;
     const currentPoke = pokemon.slice(firtsIndex, lastIndex);


     const paginado = (pageNumber) => {
      setPag(pageNumber);
    };
    //*fin*//

    useEffect(() => {
    dispatch(obtenerPokemons())
   
 

       }, [])

    return ( 
    <div className={s.contenedorPrincipal}>
        <Nav/>
         <div className={s.container}>
            <div className={s.grid}>
                {currentPoke.map(c=>(
                <Link key={c.id} className={s.link} to={"/" + c.id}>
                    <Tarjeta
                    name={ c.name}
                    imagen={ c.sprites.other.dream_world.front_default}
                    type={ c.types.map(t=>t.type.name)} 
                    />
                </Link>
            )
            )}
            </div>
            <Paginado>
            porPag={porPag}
        pokemon={pokemon.length}
        paginado={paginado}
            </Paginado>
        </div>   
    </div> 
    );
}
 
export default Principal;
