import { TYPES } from "./actions"
const initialState={
  pokemones:[],
  detail:[]
}
export default function rootReducer(state = initialState, action) {

    switch (action.type) {
        case TYPES.OBTENER_POKEMON:
        return{
           ...state,
          pokemones: action.payload
        }
        case TYPES.BUSCAR_POKEMON:
        
        return{
          ...state,
         pokemones: action.payload
       }
       case TYPES.DETALLE_POKEMON:
       return{
        ...state,
       detail: action.payload
     }
     case TYPES.ORDER_BY_NAME:
      if(action.payload==="ASCENDENTE"){
         return {
           ...state,
           pok: state.countriesFilt.sort((a, b) => {
             if (a.name < b.name) {
               return -1;
           }
           if (a.name > b.name) {
               return 1;
           }
           return 0;
         }) 
       }
      }else{
         return {
           ...state,
           pokemones: state.countriesFilt.sort((a, b) => {
             if (a.name < b.name) {
               return 1;
           }
           if (a.name > b.name) {
               return -1;
           }
           return 0;
         }) 
       }
      }
        default:
          return state;
    }
  
}
