import axios from "axios"
export const siguiente =[]
export const anterior =[]
export const TYPES={
   OBTENER_POKEMON:"OBTENER_POKEMON",
   BUSCAR_POKEMON:"BUSCAR_POKEMON",
   DETALLE_POKEMON :"DETALLE_POKEMON",
   
}

export function obtenerPokemons(url){
    return function (dispatch){
        fetch(url)
        .then(respuesta=>respuesta.json())
        .then(pokemons=> {
            siguiente.push(pokemons.next)
            anterior.push(pokemons.previous)
         
        //hago un fetch a todad las urls
       /*   Promise.all(pokemons.results.map(p=>fetch(p.url))).then(responses =>
            //el resultado los covierto a objeto
            Promise.all(responses.map(res => res.json()))) */.then(final => { 
            //finalmete los devuelvo 
            dispatch({
                    type: TYPES.OBTENER_POKEMON,
                    payload: pokemons
                    
                })
            })  
        })
        
    
    }
}






export  function buscarPokemon(nombre) {
    return  function (dispatch) {
        try {
          fetch(`https://pokeapi.co/api/v2/pokemon/${nombre}` )
          .then(response=>response.json())
          .then(poke =>{
            const array=[]
            array.push(poke)
            dispatch({
              type:TYPES.BUSCAR_POKEMON,
              payload:array
            })  
          })
        } catch (error) {
          
            console.log(error)
        }
    }
  }

  export function detallePokemon(id) {
    return  function (dispatch) {
        try {
          fetch(`https://pokeapi.co/api/v2/pokemon/${id}`)
          .then(response=>response.json())
          .then(data=>{
             dispatch({
              type:TYPES.DETALLE_POKEMON,
              payload:data
            }) 
          })
        } catch (error) {
            console.log(error)
        }
    }
  } 
 
  export function crearPokemon(payload) {
    return async function (dispatch) {
        await axios.post('http://localhost:3001/activities', payload);
        return dispatch({
            type: TYPES.POST_ACTIVITIES,
        })
    }
  }
