import Nav from "./Nav";
import { useDispatch, useSelector } from 'react-redux'
import Tarjeta from "./Tarjeta";
import s from "../css/principal.module.css"
import { Link } from "react-router-dom";
import React, { useState, useEffect } from 'react';
import { buscarPokemon, obtenerPokemons,siguiente,anterior } from "../../Redux/actions";
import Paginado from "./Paginado";
import axios from "axios"
const API_URL="https://pokeapi.co/api/v2/pokemon/?offset=20&limit=10"

const Principal = () => {

    const pokemon= useSelector((state) => state.pokemones)
    const [pagina, setPagina] = useState (1);
    const [porPagina, setPorPagina] = useState (3);
  
    const maximo = pokemon.length / porPagina;

    
    const dispatch = useDispatch()
 
    useEffect(() => {
      dispatch(obtenerPokemons(API_URL))
     
      
       }, [])

    const siguientePag=()=>{
        
    dispatch(obtenerPokemons(next))
   
    setNext(next+1)
    }

    const anteriorPag=()=>{
        dispatch(obtenerPokemons(prev))
        setPrev(prev-1)
        }
    return ( 
    <div className={s.contenedorPrincipal}>
         <Nav/>
         <div className={s.container}>
            <div className={s.grid}>
              {   
          pokemon.length ?
              pokemon.slice (
                (pagina - 1) * porPagina,
                (pagina - 1) * porPagina + porPagina
              ).map(c=>(
                <Link key={c.id} className={s.link} to={"/" + c.id}>
                    <Tarjeta
                    name={ c.name}
                    imagen={ c.sprites.other.dream_world.front_default}
                    type={ c.types.map(t=>t.type.name)}
                    />
                </Link>
            )
            ): <p>cargando...</p>
        } 
            </div>
       <Paginado pagina={pagina} setPagina={setPagina} maximo={maximo}/>
    </div> 
    </div> 
    );
}
 
export default Principal;
