export const TYPES={
   OBTENER_POKEMON:"OBTENER_POKEMON",
   BUSCAR_POKEMON:"BUSCAR_POKEMON",
   DETALLE_POKEMON :"DETALLE_POKEMON",

}
export function obtenerPokemons(){



    return function (dispatch){
        fetch(`https://pokeapi.co/api/v2/pokemon/`)
        .then(respuesta=>respuesta.json())
        .then(pokemons=> {
            console.log(pokemons);
        //hago un fetch a todad las urls
         Promise.all(pokemons.results.map(p=>fetch(p.url))).then(responses =>
            //el resultado los covierto a objeto
            Promise.all(responses.map(res => res.json()))
        ).then(final => {
            //finalmete los devuelvo 
            dispatch({
                    type: TYPES.OBTENER_POKEMON,
                    payload: final
                    
                })
            }) 
        
        })
    
    }
}

export  function buscarPokemon(nombre) {
    return  function (dispatch) {
        try {
          fetch(`https://pokeapi.co/api/v2/pokemon/${nombre}` )
          .then(response=>response.json())
          .then(poke =>{
            const array=[]
            array.push(poke)
            dispatch({
              type:TYPES.BUSCAR_POKEMON,
              payload:array
            })  
          })
        } catch (error) {
          
            console.log(error)
        }
    }
  }
