import axios from "axios"
export const siguiente =[]
export const anterior =[]
export const TYPES={
   OBTENER_POKEMON:"OBTENER_POKEMON",
   BUSCAR_POKEMON:"BUSCAR_POKEMON",
   DETALLE_POKEMON :"DETALLE_POKEMON",
   
}


export function obtenerPokemons(url){
    return async function (dispatch){
     const data= await axios.get(url)
      const result=0
      const db=[]
      const api=[]
     db.push(data.data[1])
     api.push(data.data[0].props.arrayPokemons) 
      result= [db,...api]
     console.log(result);
     dispatch({
      type: TYPES.OBTENER_POKEMON,
      payload: db
     }) 
    }
}



export  function buscarPokemon(nombre) {
    return  function (dispatch) {
        try {
          fetch(`hhttps://pokeapi.co/api/v2/pokemon/${nombre}` )
          .then(response=>response.json())
          .then(poke =>{
            const array=[]
            array.push(poke)
            console.log(array)
            dispatch({
              type:TYPES.BUSCAR_POKEMON,
              payload:array[0]
            })   
          })
        } catch (error) {
          
            console.log(error)
        }
    }
  }

  export function detallePokemon(id) {
    return  function (dispatch) {
        try {
          fetch(`https://pokeapi.co/api/v2/pokemon/${id}`)
          .then(response=>response.json())
          .then(data=>{
             dispatch({
              type:TYPES.DETALLE_POKEMON,
              payload:data
            }) 
          })
        } catch (error) {
            console.log(error)
        }
    }
  }
