import axios from "axios"
export const siguiente =[]
export const anterior =[]
export const TYPES={
   OBTENER_POKEMON:"OBTENER_POKEMON",
   BUSCAR_POKEMON:"BUSCAR_POKEMON",
   DETALLE_POKEMON :"DETALLE_POKEMON",
   
}


export function obtenerPokemons(url){
  try {
    return  function (dispatch) {
    fetch(url)
    .then(response=>response.json())
    .then(data=>{
      Promise.all(data.results.map(u=>fetch(u.url))).then(responses =>
        Promise.all(responses.map(res => res.text()))
    ).then(texts => {
      console.log(texts);
    })
    /*   var array=[]
      array.push(data.results)
         dispatch({
        type:TYPES.OBTENER_POKEMON,
        payload:array[0]
      })    */
    })
  }
  } catch (error) {
      console.log(error)
  }
}




export  function buscarPokemon(nombre) {
    return  function (dispatch) {
        try {
          fetch(`hhttps://pokeapi.co/api/v2/pokemon/${nombre}` )
          .then(response=>response.json())
          .then(poke =>{
            const array=[]
            array.push(poke)
            console.log(array)
            dispatch({
              type:TYPES.BUSCAR_POKEMON,
              payload:array[0]
            })   
          })
        } catch (error) {
          
            console.log(error)
        }
    }
  }

  export function detallePokemon(id) {
    return  function (dispatch) {
        try {
          fetch(`https://pokeapi.co/api/v2/pokemon/${id}`)
          .then(response=>response.json())
          .then(data=>{
             dispatch({
              type:TYPES.DETALLE_POKEMON,
              payload:data
            }) 
          })
        } catch (error) {
            console.log(error)
        }
    }
  }
